import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function RoomSchedule() {
    const [reservations, setReservations] = useState([]);
    const roomId = window.location.pathname.split("/").pop();
    const date = new URLSearchParams(window.location.search).get("date");
    const token = localStorage.getItem("token");
    const navigate = useNavigate();

    const HOUR_HEIGHT = 50;
    const [pendingTime, setPendingTime] = useState(null);

    function handleTimelineClick(e) {
        const rect = e.currentTarget.getBoundingClientRect();
        const y = e.clientY - rect.top;

        const hours = Math.floor(y / HOUR_HEIGHT);
        const minutes = Math.floor(((y % HOUR_HEIGHT) / HOUR_HEIGHT) * 60);

        const hh = String(hours).padStart(2, "0");
        const mm = String(minutes - (minutes % 15)).padStart(2, "0");

        setPendingTime(`${hh}:${mm}`);
    }

    useEffect(() => {
        fetch(`http://localhost:5000/api/schedule?room_id=${roomId}&date=${date}`, {
            headers: { Authorization: `Bearer ${token}` }
        })
            .then(res => res.json())
            .then(data => setReservations(data || []));
    }, [roomId, date, token]);

    return (
        <div style={{ padding: "1rem" }}>
            <h2>Room {roomId} ‚Äì {date}</h2>

            {/* üü© OPRAVEN√ù BACK */}
            <button onClick={() => navigate("/")}>
                ‚Üê Back
            </button>

            {pendingTime && (
                <div style={{ margin: "1rem 0" }}>
                    <b>Selected time:</b> {pendingTime}

                    {/* üü© OPRAVEN√ù BOOK */}
                    <button
                        style={{ marginLeft: "1rem" }}
                        onClick={() => {
                            navigate(`/?book_room=${roomId}&date=${date}&time=${pendingTime}`, {
                                replace: true
                            });
                        }}
                    >
                        Book
                    </button>
                </div>
            )}

            <div
                onClick={handleTimelineClick}
                style={{
                    position: "relative",
                    borderLeft: "1px solid #555",
                    marginTop: "1rem",
                    height: 24 * HOUR_HEIGHT,
                    width: "100%",
                    cursor: "pointer",
                }}
            >
                {[...Array(24)].map((_, h) => (
                    <div
                        key={h}
                        style={{
                            height: HOUR_HEIGHT,
                            borderTop: "1px solid #444",
                            paddingLeft: "5px",
                            color: "#777"
                        }}
                    >
                        {h}:00
                    </div>
                ))}

                {reservations.map((r, i) => {
                    function offset(time) {
                        const [h, m] = time.split(":").map(Number);
                        return h * HOUR_HEIGHT + (m / 60) * HOUR_HEIGHT;
                    }

                    return (
                        <div
                            key={i}
                            onClick={(e) => e.stopPropagation()}   // üü© BLOKNE KLIK
                            style={{
                                position: "absolute",
                                top: offset(r.start_time),
                                height: offset(r.end_time) - offset(r.start_time),
                                left: "100px",
                                right: "20px",
                                background: "#d47cb3",
                                opacity: 0.8,
                                borderRadius: "4px",
                                padding: "4px",
                                cursor: "default",                   // üü© uk√°≈æe ≈æe sa ned√° klikn√∫≈•
                                zIndex: 10,                           // üü© nech je blok nad ƒçasovou mrie≈ækou
                            }}
                        >
                            Reserved
                        </div>
                    );
                })}

            </div>
        </div>
    );
}
