  const handleCancel = async (room) => {
  const resList = room.allReservations;

  if (!resList || resList.length === 0) {
    alert("This room has no reservations for selected day.");
    return;
  }

  // v칳ber rezerv치cie
  const msg = resList.map((r, i) =>
    `${i + 1}) ${r.start} - ${r.end}`
  ).join("\n");

  const choice = prompt(
    `Which reservation do you want to cancel?\n\n${msg}\n\nEnter number:`
  );

  const index = Number(choice) - 1;

  if (index < 0 || index >= resList.length) {
    alert("Invalid selection.");
    return;
  }

  const selected = resList[index];

  const confirmCancel = window.confirm(
    `Cancel reservation?\n\n${room.room_number}\n${selected.start} - ${selected.end}`
  );

  if (!confirmCancel) return;

  try {
    const res = await fetch(
      `${API_URL}/api/cancel-reservation/${selected.reservation_id}`,
      {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${token}`,
          "Content-Type": "application/json"
        }
      }
    );

    const data = await res.json();

    if (!res.ok) {
      alert(data.error || "Cancellation failed");
      return;
    }

    alert("Reservation canceled");

    // reload rooms
    setRooms(prev =>
      prev.map(r =>
        r.room_id === room.room_id
          ? { ...r, status: "free", active_reservation_id: null }
          : r
      )
    );

  } catch (err) {
    console.error(err);
    alert("Server connection error");
  }
};

  const today = new Date().toISOString().slice(0, 10);
  const blockPastDate = selectedDate < today;
  const blockPastTime =
    selectedDate === today && startTime < getMinTime();
  const blockBooking = blockPastDate || blockPastTime;

  return (
    <div style={{ padding: "1rem" }}>
      <h2>Room Reservations</h2>

      {/* Search */}
      <div>
        <label>
          Search:
          <input
            type="text"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            style={{ marginLeft: "0.5rem" }}
          />
        </label>
      </div>

      {/* Date & Time */}
      <div style={{ margin: "1rem 0" }}>
        <label>
          Date:
          <input
            type="date"
            value={selectedDate}
            min={today}
            onChange={(e) => setSelectedDate(e.target.value)}
            style={{ marginLeft: "0.5rem" }}
          />
        </label>

        <label style={{ marginLeft: "1rem" }}>
          Time:
          <input
            type="time"
            value={startTime}
            min={getMinTime()}
            onChange={(e) => setStartTime(e.target.value)}
            style={{ marginLeft: "0.5rem" }}
          />
        </label>

        <label style={{ marginLeft: "1rem" }}>
          Duration:
          <select
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
            style={{ marginLeft: "0.5rem" }}
          >
            <option value="15 minutes">15 minutes</option>
            <option value="30 minutes">30 minutes</option>
            <option value="1 hour">1 hour</option>
            <option value="1.5 hours">1.5 hours</option>
            <option value="2 hours">2 hours</option>
            <option value="24 hours">Whole day</option>
          </select>
        </label>
      </div>

      {/* Filter */}
      <div style={{ marginBottom: "1rem" }}>
        <button onClick={() => setFilter("all")}>All</button>
        <button onClick={() => setFilter("free")}>Free</button>
        <button onClick={() => setFilter("occupied")}>Occupied</button>
      </div>

      {/* Suggested slot */}
      {suggestedSlot && (
        <div style={{ color: "green" }}>
          Next free slot: <b>{suggestedSlot}</b>
        </div>
      )}

      {/* TABLE */}
      {loading ? (
        <p>Loading...</p>
      ) : (
        <table border="1" cellPadding="8" width="100%">
          <thead>
            <tr>
              <th>Room</th>
              <th>Capacity</th>
              <th>Floor</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>

          <tbody>
            {filteredRooms.map((r) => (
              <tr key={r.room_id}>
                <td>{r.room_number}</td>
                <td>{r.capacity}</td>
                <td>{r.floor}</td>
                <td>{r.status}</td>

                <td>
                  {/* Book */}
                  {r.status === "free" && canBook && !blockBooking && (
                    <button onClick={() => handleBook(r.room_id)}>Book</button>
                  )}

                  {/* Cancel */}
                  {r.status === "occupied" && canDelete && (
                    <button onClick={() => handleCancel(r)}>Cancel</button>
                  )}

                  {/* Schedule */}
                  <button
                    style={{ marginLeft: "0.5rem" }}
                    onClick={() =>
                      navigate(`/schedule/${r.room_id}?date=${selectedDate}`)
                    }
                  >
                    Schedule
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
