        { method: "DELETE", headers: { Authorization: `Bearer ${token}` } }
      );

      if (res.ok) {
        alert("Reservation canceled");
        setRooms((prev) =>
          prev.map((r) =>
            r.room_id === room.room_id
              ? { ...r, status: "free", active_reservation_id: null }
              : r
          )
        );
