import React, { useEffect, useState } from "react";
import "./App.css";
import Miestnosti from "./Miestnosti";
import Login from "./Login";

function App() {
  const [role, setRole] = useState(null);

  useEffect(() => {
    // Z URL alebo localStorage získa JWT token po prihlásení
    const params = new URLSearchParams(window.location.search);
    const token = params.get("token") || localStorage.getItem("token");

    if (token) {
      localStorage.setItem("token", token);
      try {
        const payload = JSON.parse(atob(token.split(".")[1]));
        setRole(payload.role);
      } catch (e) {
        console.error("Invalid token");
        localStorage.removeItem("token");
      }
    }
  }, []);

  const logout = () => {
    localStorage.removeItem("token");
    setRole(null);
  };

  if (!role) return <Login />; // nepřihlásený user

  return (
    <div className="App">
      <h1>BookMyRoom</h1>
      <p>Rola: <b>{role}</b></p>
      <button onClick={logout}>Odhlásiť</button>

      {role === "viewer" && (
        <>
          <h3>Len prezeranie miestností</h3>
          <Miestnosti />
        </>
      )}

      {role === "employer" && (
        <>
          <h3>Rezervácie</h3>
          <Miestnosti canBook />
        </>
      )}

      {role === "admin" && (
        <>
          <h3>Administrácia</h3>
          <button>Pridať používateľa</button>
          <button>Pridať miestnosť</button>
          <Miestnosti canBook canDelete />
        </>
      )}
    </div>
  );
}

export default App;
